mapper.insert:
INSERT INTO notice
VALUES (
	null
	, #{noticeTitle}
	, #{noticeContent}
	, now()
)

mapper.update:
UPDATE notice
SET 
	noticeTitle = #{noticeTitle }
	, noticeContent = #{noticeContent }
WHERE noticeIdx = #{noticeIdx };

