-- selectEventDetailList SELECT e.event_idx , e.event_title , e.event_startDt , e.event_endDt , e.event_poster , e.event_thumbnail , e.event_category , ec.eventCate_Subject FROM event e JOIN eventCate ec ON e.event_category = ec.eventCate_Idx
WHERE eventCate_Idx = #{eventCate_Idx}
LIMIT #{startRow} , #{listLimit}
