mapper.insert:
INSERT INTO review
VALUES (
	NULL
	, #{review_rating}
	, #{review_content}
	, now()
	, #{movie_code}
	, #{user_id}
)

mapper.select:
SELECT *
FROM review
WHERE movie_code = #{movie_code}
ORDER BY review_num DESC
LIMIT #{startRow}
	, #{listLimit}

mapper.delete:
DELETE FROM review 
WHERE user_id = #{user_id} 
	AND movie_code = #{movie_code}
	AND review_num = #{review_num}

mapper.update:
UPDATE review
SET review_rating = #{review_rating1}
	, review_content = #{review_content}
	, review_date = now()
WHERE review_num = #{review_num}
	AND user_id = #{user_id}
	AND movie_code = #{movie_code}
