-- selectStoreDetailList SELECT s.store_idx , s.store_short , s.store_title , s.store_price , s.store_thumbnail , s.store_category , sc.storeCate_Subject FROM store s JOIN storeCate sc ON s.store_category = sc.storeCate_Idx
WHERE storeCate_Idx = #{storeCate_Idx}
LIMIT #{startRow} , #{listLimit}
